 ["$scope", "$timeout", "$document", "popup", function ($scope, $timeout, $document, popup) {
    $scope.fullscreen = function () {
        if (!$scope.$ViewFullScreen) {
            popup("/board/main", {
                $ViewFullScreen: true,
                $ViewStyle: {
                    position: 'fixed',
                    left: 0,
                    right: 0,
                    bottom: 0,
                    top: 0,
                    borderRadius: 0,
                    width: "100%",
                    height: "100%",
                    backgroundColor: "#fff"
                }
            });
        } else {
            $scope.$CloseView();
        }
    };
    var datas=[

    ];

    var option = {
        backgroundColor: '#eee',
        title: {
            text: '业务员销售排名',
            textStyle: {
                fontWeight: 'normal',
                fontSize: 14
            }
        },
        tooltip: {
            trigger: 'axis'
        },
        calculable: true,
        xAxis: [
            {
                type: 'category',
                data: ['1月', '2月', '3月', '4月', '5月', '6月', '7月', '8月', '9月', '10月', '11月', '12月'],
                axisLabel: {
                    formatter: function (p) {
                        return p;
                    }
                }
            }
        ],
        yAxis: [
            {
                type: 'value'
            }
        ],
        series: [
            {
                name: '蒸发量',
                type: 'bar',
                data: [2.0, 4.9, 7.0, 23.2, 25.6, 76.7, 135.6, 162.2, 32.6, 20.0, 6.4, 3.3],
                markPoint: {
                    data: [
                        {type: 'max', name: '最大值'},
                        {type: 'min', name: '最小值'}
                    ]
                },
                markLine: {
                    data: [
                        {type: 'average', name: '平均值'}
                    ]
                }
            }
        ]
    };
    $document.ready(function () {
        var chartarea = document.getElementById($scope.$ViewFullScreen ? 'canvasBoardFullScreen' : 'canvasBoardNormal');
        var charts = [].map.apply(chartarea.children, [function (div) {
                var mychart = echarts.init(div);
                mychart.setOption(option);
                return mychart;
            }]);
        var resize = function () {
            charts.forEach(function (chart) {
                chart.resize();
            });
        };
        $scope.$watch(function () {
            return chartarea.offsetWidth;
        }, resize);
    });
