controller:
["$scope", "$document", function($scope,$document){
        document.body.style["-webkit-filter"] = "blur(15px)";
        document.body.style["-moz-filter"] = "blur(15px)";
        document.body.style["-ms-filter"] = "blur(15px)";
        document.body.style["filter"] = "blur(15px)";
        $scope.$on("$destroy",function(){
            document.body.style["-webkit-filter"] = "";
            document.body.style["-moz-filter"] = "";
            document.body.style["-ms-filter"] = ")";
            document.body.style["filter"] = "";

        });
    angular.extend($scope.$ViewStyle,{
        left:"0",
        right:"0",
        top:"0",
        bottom:"0",
        background:"transparent",
        color:"#fff",
        width:"100%",
        height:"100%"
    });
    $scope.$ViewTitle=false;
    $scope.$ViewDragable=false;
}]
