        var data = $scope.option_groups.list;
        popup('/operat/all', {
            template: template,
            repeat: "option",
            data: data,
            opendata: function (cx) {
                var obj = data[cx];
                obj.origin = cx;
                obj.isClick = !obj.isClick;
                for (var dx = $scope.option_common.length - 1; dx >= 0; dx--) {
                    if (obj.origin === $scope.option_common[dx].origin) {
                        $scope.option_common.splice(dx, 1);
                    }
                }
                if (obj.isClick) {
                    $scope.option_common.splice(0, 0, obj);
                }
