controller:
 ["$scope", "alert", "cache", function ($scope,alert,cache){
	var num=$scope.data.num;
	$scope.shops=new Array();
	for(var i=0;i<num;i++){
		$scope.shops.push({
			id:-1,
			cj:"3",
			lx:"3",
			pbm:$scope.data.pbm,
			name:"",
			country:"中国",
			province:"",
			city:"",
			town:"",
			dz:"",
			area:"",
			rental:"",
			fzrdm:"",
			require:false
		})
	}
	$scope.isshow=false;
	
	$scope.fzrlist=cache.userlist;
	
	//获取身份
	api("/x6/getProvinceList.do",{})
			.onsuccess(function(data){
				for(var i=0;i<data.list.length;i++){
					data.list[i]=data.list[i].province;
				}

				$scope.provs=data.list;
			})
			
	//获取城市
	$scope.ckey=[];
	$scope.citys={};
	$scope.getCityList=function(obj,index){
		$scope.shops[index].city="";
		$scope.shops[index].town="";
		var param={
			province:obj
		}
		$scope.ckey[index]=obj;
		if(!$scope.citys[obj]){
			api('/x6/getCityList.do',param)
			.onsuccess(function(data){
				for(var i=0;i<data.list.length;i++){
					data.list[i]=data.list[i].city;
				}
				$scope.citys[obj]=data.list;
			})
		}
	}
	//获取县区
	$scope.akey=[];
	$scope.areas={};
	$scope.getTownList=function(obj,index){
		$scope.shops[index].town="";
		var param={
			city:obj
		}
		//console.log(obj)
		$scope.akey[index]=obj;
		if(!$scope.areas[obj]){
			api('/x6/getTownList.do',param)
			.onsuccess(function(data){
				for(var i=0;i<data.list.length;i++){
					data.list[i]=data.list[i].town;
				}
				$scope.areas[obj]=data.list;
			})
		}
	}
	
	$scope.$ViewOptions={
		"确认":function(){
			$scope.isshow=true;
			//验证输入合法性
			var required=[
				{
					name:"name",
					msg:"公司名称不能为空"
				},
				{
					name:"province",
					msg:"省份不能为空"
				},
				{
					name:"city",
					msg:"城市不能为空"
				},
				{
					name:"town",
					msg:"县/区不能为空"
				},
				{
					name:"dz",
					msg:"详细地址不能为空"
				}
			];
			
			var temp=verify(required,$scope.shops);
			if(temp){
				for(var i=0;i<$scope.shops.length;i++){
					$scope.shops[i].fzrdm=$scope.shops[i].fzrdm.value;
					if(!$scope.shops[i].area){
						$scope.shops[i].area=0;
					}
					if(!$scope.shops[i].rental){
						$scope.shops[i].rental=0;
					}
				}
				console.log($scope.shops)
				api('/x6/bathSaveXtGsxx.do',{rows:$scope.shops}).onsuccess(function(data){
					if(data.result=="ok"){
						alert.success("新增成功");
						return false;
					}else{
						alert.warn(data.message);
						return false;
					}
				})
				
			}else{
				return false;
			}
			//return false;
		},
        "关闭":function(){
                    
        }
	}
	
	
	
	
	
	
	
	
	
	
	
	
	function getElement(row,col){
		var target=document.getElementsByClassName("container-items")[row].childNodes;
		var _left=target[2*col+3].getBoundingClientRect().left;
		var _top=target[2*col+3].getBoundingClientRect().top;
		return {
			left:_left,
			top:_top
		}
	}
	function verify(required,obj){
		for(var i = 0; i < obj.length; i++) {
			for(var j = 0; j < required.length; j++) {
				//当检测到有空值的时候
				if(!obj[i][required[j].name]) {
					//console.log(333)
					obj[i].require = false;
					var place = getElement(i, j);
					alert.require(required[j].msg, place.top, place.left);
					//break;
					return false;
					
				} else {
					obj[i].require = true;
				}
			}
		}
		return true;
	}
}]
