controller:
 ["$scope", "alert", "cache", function ($scope,alert,cache){
	var sign=0;
	console.log($scope.gslist)
	//获取详情
	api("/x6/getXgGsDetail.do",{
        gsdm:$scope.data.gsdm,
        id:$scope.data.id
    }).onsuccess(function(res){
        angular.extend($scope.data,res.gsxx);
        //console.log($scope.data)
        $scope.shop={
			id:$scope.data.id,
			name:$scope.data.name,
			province:$scope.data.province,
			city: $scope.data.city,
			town: $scope.data.town,
			dz: $scope.data.dz,
			fzrdm: "",
			yxbz:$scope.data.yxbz,//0启用 1不启用
			area:"",
			rental:"",
			pbm:$scope.data.pbm
		}
        for(var i=0;i<$scope.gslist.length;i++){
			if($scope.gslist[i].bm==$scope.data.pbm){
				$scope.ssgsname=$scope.gslist[i];
			}
		}
        //$scope.shop.ssgsname="asaseddd"
        //console.log($scope.shop)
    });
	
	
	//负责人列表
	$scope.fzrlist=cache.userlist;
	$scope.$ViewOptions={
		"确认":function(){
			//验证输入合法性
			var required=[
				{
					name:"name",
					msg:"公司名称不能为空"
				},
				{
					name:"province",
					msg:"省份不能为空"
				},
				{
					name:"city",
					msg:"城市不能为空"
				},
				{
					name:"town",
					msg:"县/区不能为空"
				},
				{
					name:"dz",
					msg:"详细地址不能为空"
				}
			];
			
			var temp=verify(required,$scope.shop);
			if(temp){
				$scope.shop.area=$scope.shop.area?$scope.shop.area:0;
				$scope.shop.rental=$scope.shop.rental?$scope.shop.rental:0;
				delete $scope.shop.ssgsname;
				
				api('/x6/saveXtgs.do',$scope.shop).onsuccess(function(data){
					if(data.result=="ok"){
						alert.success("编辑成功");
					}else{
						alert.warn(data.message);
						return false;
					}
				})
			}else{
				return false	
			}
		},
        "关闭":function(){
                    
        }
	}
	
	$scope.getSsgs=function(obj){
		if(typeof obj!="string"){
			$scope.shop.pbm=obj.bm;
		}
	}
	
	
	
	
	
	
	
	
	
	
	//获取身份
	api("/x6/getProvinceList.do",{})
			.onsuccess(function(data){
				for(var i=0;i<data.list.length;i++){
					data.list[i]=data.list[i].province;
				}

				$scope.provs=data.list;
			})
	//获取城市

	$scope.getCityList=function(obj,index){
		sign++;
		if(sign>1){
			$scope.shop.city="";
			$scope.shop.town="";
		}
		var param={
			province:obj
		}
		api('/x6/getCityList.do',param)
			.onsuccess(function(data){
				for(var i=0;i<data.list.length;i++){
					data.list[i]=data.list[i].city;
				}
				$scope.citys=data.list;
			})
	}
	
	//获取县区

	$scope.getTownList=function(obj,index){
		sign++;
		if(sign>2){
			$scope.shop.town="";
		}
		
		var param={
			city:obj
		}
		api('/x6/getTownList.do',param)
			.onsuccess(function(data){
				for(var i=0;i<data.list.length;i++){
					data.list[i]=data.list[i].town;
				}
				$scope.areas=data.list;
			})
	}
	
	
	
	function getElement(row,col){
		var target=document.getElementsByClassName("edit-items")[row].childNodes;
		var _left=target[col].getBoundingClientRect().left;
		var _top=target[col].getBoundingClientRect().top;
		return {
			left:_left,
			top:_top
		}
	}
	function verify(required,obj){
		for(var j = 0; j < required.length; j++) {
				//当检测到有空值的时候
				var row,col;
				if(!obj[required[j].name]) {
					//console.log(j)
					if(j==0){
						row=0;
						col=3;
					}else{
						row=1;
						col=2*j+1;
					}
					var place=getElement(row,col)
					alert.require(required[j].msg, place.top, place.left);
					//break;
					return false;
				}
			}
		return true;
	}
	
}]
