controller:
 ["$scope", "views", "popup", "tasks", "$timeout", "excel", "loading", "cache", "alert", "tips", function ($scope,views,popup,tasks,$timeout,excel,loading,cache,alert,tips) {
    loading.coffee();
    var listed;
    $scope.file=function(){
        excel();
    };
    $scope.filter=false;
    $scope.splx=cache.splxList;

    $scope.qyValue={
        qy:1,
        noqy:0
    }
    //获取货品列表
	var qylist;
    var noqylist;
    api("/x6/getSpxxList.do",{
    	yxbz:0
	}).onsuccess(function(data){
        api("/x6/getSpxxList.do",{
            yxbz:1
        }).onsuccess(function(data){
            loading.coffeeEnd(1000);
            noqylist=data.spxxList;
            for(var i=0;i<noqylist.length;i++){
                if(JSON.parse(noqylist[i].tags).length){
                    var tags="";
                    JSON.parse(noqylist[i].tags).map(function(i){
                        tags+=","+i.name;
                    })
                    noqylist[i].tags=tags.substring(1);
                }else{
                    noqylist[i].tags=""
                }
            }
        })
  		qylist=data.spxxList;
    	for(var i=0;i<qylist.length;i++){
			// cache.splxList.map(function(n){
			// 	if(n.bm==qylist[i].splx){
             //        qylist[i].splx={name:n.name}
			// 	}
			// });
			//console.log(JSON.parse(data.spxxList[i].tags))
			if(JSON.parse(qylist[i].tags).length){
				var tags="";
				JSON.parse(qylist[i].tags).map(function(i){
					tags+=","+i.name;
				})
                qylist[i].tags=tags.substring(1);
			}else{
                qylist[i].tags=""
			}
   		}
        $scope.goods=qylist;
    });

    //选择启用/不启用
    $scope.isqy=function () {
        $scope.goods=[];
        if($scope.qyValue.qy&&$scope.qyValue.noqy){
            $scope.goods=$scope.goods.concat(qylist,noqylist);
        }else if($scope.qyValue.qy&&!$scope.qyValue.noqy){
            $scope.goods=qylist;
        }else if(!$scope.qyValue.qy&&$scope.qyValue.noqy){
            $scope.goods=noqylist;
        }else{
            $scope.goods=[];
        }
    }

    //获取商品分类
    $scope.items = cache.splxList;//最多六项
	//点击商品分类
	$scope.active_item=sessionStorage.getItem("activ$scope.active_itemeIndex")?sessionStorage.getItem("activeIndex"):0;

    $scope.active = function (v,index) {
          $scope.active_item=index
          sessionStorage.setItem("activeIndex",index);
    } 

	//删除商品分类
	$scope.delProductType=function(v,index){
		api("/x6/deleteSplx.do",{
			id:v.id
		}).onsuccess(function(data){
			//$scope.items.splice(index,1);
			//回存到cache
			//cache.save();
			cache.refresh();
			$scope.items=cache.splxList;
			//$scope.$apply();
			//console.log(cache)
		})
	}
	//新增商品分类
//	if($scope.isadd){
//		console.log(4545)
//	}
	$scope.isadd=false;
	$scope.edithide=false;
	$scope.addProductType=function(name,obj,index){
		if(name){
			api("/x6/saveSplx.do",{
				id:obj?obj.id:-1,
				name:name
			}).onsuccess(function(data){
				var msg;
				if(obj){
					msg="修改成功";
					obj.edit=false;
					$scope.items[index].name=name;
					$scope.active_item=index;
				}else{
					msg="新增成功";
					$scope.items.push(data.splx);
					$scope.typeName="";
				}
				cache.refresh();
				tips.success(msg);
				//console.log(cache)
			})
		}
	}
	//按回车提交
	$scope.addevent=function(name){
		document.body.addEventListener('keyup',press(name))
	}

	$scope.add=function(){
        popup('/setting/info/goods_add',{
	            '$ViewTitle':'新增货品信息',
	            '$ViewOptions':{
	                "保存":function(close){
	                    return false;
	                },
	                "关闭":function(){
	                    //return false;
	                }
	            },
	            data:$scope.goods
	        },
        	{
        		width:750,
        		height:450
        	}
        )
    };

      $scope.edit=function(value,key){
        popup('/setting/info/goods_edit',{
            '$ViewTitle':'编辑货品信息',
            '$ViewOptions':{
                "保存":function(close){
                    return false;
                },
                "关闭":function(){
                    return false;
                }
            },
            data:value
        },{width:750,height:450}).onclose(function(){
        });
    };

	$scope.delProducts=function(){
        alert("消息","确定删除吗?",{
        	  '确定': function() {       	  	
					var ids="";
			    	for(var i=0,len=$scope.checkedRow.length;i<len;i++){
			    		ids+=","+$scope.checkedRow[i].id;
			    	}
			    	api("/x6/deleteSpxxBatch.do",{
			    		ids:ids.substring(1)
			    	}).onsuccess(function(data){
			    		var params={
			    			yxbz:$scope.qy?0:1
			    		}
			    		api("/x6/getSpxxList.do",params).onsuccess(function(data){
					    	$scope.goods=data.spxxList;
					    	for(var i=0;i<$scope.goods.length;i++){
								cache.splxList.map(function(n){
									if(n.bm==$scope.goods[i].splx){
										$scope.goods[i].splx={name:n.name}
									}
								});
								//console.log(JSON.parse(data.spxxList[i].tags))
								if(JSON.parse($scope.goods[i].tags).length){
									var tags="";
									JSON.parse($scope.goods[i].tags).map(function(i){
										tags+=","+i.name;
									})
									$scope.goods[i].tags=tags.substring(1);
								}else{
									$scope.goods[i].tags=""
								}
					   		}
					    })
			    	})
			   },
				'取消': function() {}
        })
    };
    //修改库龄预警天数
	$scope.editDays=function(){
		popup("/setting/info/goods_days",{
			'$ViewTitle':'编辑货品信息',
			'$ViewOptions':{
                "保存":function(close){
                    return false;
                },
                "关闭":function(){
                    return false;
                }
           },
           data:$scope.checkedRow
		},{
			width:400,
			height:200
		})
	}

	function getList(url,params){
		var promise=new Promise(function(reslove,reject){
			api(url,params).onsuccess(function(data){
				reslove(data);
			})
		});
		return promise;
	}
	function press(name){
		console.log(event)
		console.log(name)
//		if(event.keyCode==13&&$scope.typeName){
//			//console.log(45454)
//			$scope.addProductType($scope.typeName);
//		}
	}
}]
