controller:
["$scope", "clone", "tips", "$filter", function($scope,clone,tips,$filter){
	$scope.tc=clone.object($scope.data.tc);
	$scope.yyslist=$scope.data.list;
    for(var i=0;i<$scope.yyslist.length;i++){
    	if($scope.yyslist[i].id==$scope.tc.yysdm){
            $scope.tc.yysdm={
            	name:$scope.yyslist[i].name,
				yysdm:$scope.yyslist[i].id
			}
		}
	}
	//保存
	$scope.$ViewOptions={
    	"确认":function () {
			var tempobj=clone.object($scope.tc);
			tempobj.yysdm=tempobj.yysdm.yysdm;
			api("/x6/saveTclx.do",tempobj).onsuccess(function (data) {
                for(key in $scope.data.tc){
                    $scope.data.tc[key]=data.tclx[key];
				}
            })
        },
		"取消":function () {

        }
	}
    //$scope.tc.yysdm=$scope.tc.yysname.name;

}]
