controller:
 ["$scope", "api", function ($scope, api) {
    //保存系统参数
    var save = function (cs) {
        api("/x6/saveXtcs.do", {
            id: cs.id,
            gsdm: cs.gsdm,
            value: cs.value
        }).success(function (result) {
            $timeout($scope.$CloseView,100);
        });
    };
    $scope.cs = {};
    angular.copy($scope.xtcs, $scope.cs);
    $scope.$ViewOptions = {
        "保存": function () {
            save($scope.cs);
            return false;
        },
        "关闭": function () {

        }
    };
    $scope.save=save;
    $scope.$ViewTitle="编辑系统参数";
    console.log($scope);
}]
