    ];
    var opened = tasks();
    -function () {
        var map = {};
        opened.forEach(function (task) {
            var name = task.name || task;
            map[name] = true;
        });
        $scope.option_groups.forEach(function (option_group) {
            option_group.list.forEach(function (option) {
                if (map[option.name]) {
                    option.intask = true;
                }
            });
        });
    }();
    $scope.opened = opened;
    $scope.open = function (option) {
        views.main.open("/setting/detail").then(function () {
            views.main.data({"active_task": option, "option_groups": $scope.option_groups}).currentView.$Save("active_task", "option_groups");
        });
        tasks.on(option);
        tasks.save();
    };
}]
