 ["$scope", "getRootElementInPopup", "$document", "popup", "alert", "api", "user", "alert", "getScreenPosition", "$compile", function ($scope, getRootElementInPopup, $document, popup, alert, api, user, alert, getScreenPosition, $compile) {
    //点击空白处后，关闭窗口
    var close_time = 0;
    var onmouseup = function (event) {
        var popup = getRootElementInPopup(event.target);
        if (!popup) {
            close_time = setTimeout(function () {
                if (event.prevented) {
                    return;
                }
                if (
                        view //存在弹窗
                        ) {
                    //关闭弹窗
                    history.back();
                }
            }, 20);
        }
    };
    $document.on('mouseup', onmouseup);
    $scope.$on("$destory", function () {
        $document.off('mouseup', onmouseup);
        view && view.close();
    });

    var view;

    //编辑个人信息
    $scope.edit = function ($, $data) {
        clearTimeout(close_time);
        if (view) {
            view.data({data: $});
            return;
        }
        view = popup('/setting/staff/edit', {
            data: $,
            $ViewStyle: {
                position: "absolute",
                left: "0",
                top: '0',
                height: "100%",
                width: "100%",
                border: '1px solid #e2e3e5',
                borderRadius: '1px',
                boxShadow: '0 0 5px rgba(0,0,0,0.1)'
            }
        }).css({
            right: '10px',
            top: '110px',
            left: '400px',
            bottom: '0px',
            background: 'transparent'
        }).nobackground().solid().right().onclose(function () {
            view = null;
        });
    };

    //新增与员工信息
    $scope.add = function () {
        popup("/setting/staff/add", {
            "$ViewTitle": "新增员工信息"
        }, {width: 800, height: 607}).onclose(function (userInfo) {
            if (userInfo) {
                var bz = userInfo.yxbz;
                $scope.staffs.splice(0, 0, userInfo);
                var arr = $scope.yx_staffs[yxbz[bz]];
                arr && arr.splice(0, 0, userInfo);
            }
        });
    };



    $scope.filter = function () {
        $scope.filter.ing = !$scope.filter.ing;
    };


    //显示状态
    $scope.xschoose = false;
    $scope.displayChoose = function () {
        $scope.xschoose = true;
    };
    $scope.hideChoose = function () {
        $scope.xschoose = false;
    };
//    $scope.geren_template = "<span ng-click=edit($) style='cursor:pointer;color:#4385f5;padding:0 10px;display:inline-block;max-width:220px;width:100%;text-align:right'><span style=float:left>{{$.name}}</span>&nbsp;<span>{{$.phone}}</span></span>";


    $scope.yxbz = {
        zz: 1,
        lz: 0,
        wkt: 0
    };
    var yxbz = {
        "0": "zz",
        "1": "lz",
        "-1": "wkt"
    };
    //重组表格数据
    var rebuild = function () {
        var staffs = [];
        var yx_staffs = $scope.yx_staffs;
        for (var k in yxbz) {
            var kk = yxbz[k];
            if ($scope.yxbz[kk]) {
                if (yx_staffs[kk]) {
                    staffs.push.apply(staffs, yx_staffs[kk]);
                } else {
                    getUserList(k, kk);
                }
            }
        }
        $scope.staffs = staffs;
    };
    //重建源数据
    var refresh = function () {
        var yx_staffs = $scope.yx_staffs;
        for (var k in yxbz) {
            var kk = yxbz[k];
            if ($scope.yxbz[kk]) {
                getUserList(k, kk);
            } else {
                delete yx_staffs[kk];
            }
        }
    };
    //登录
    user.onlogin = refresh;
    $scope.yx_staffs = {};
    $scope.$watch('yxbz', rebuild, true);
    $scope.$watch("yx_staffs", rebuild);
    //员工列表
    var getUserList = function (k, kk) {
        $scope.staffs = [];
        var params = {
            yxbz: k//有效标志
        };
        api('/x6/loadUserList.do', params).onsuccess(function (data) {
            var temp = $scope.yx_staffs;
            var yx_staffs = {};
            yx_staffs[kk] = data.userList;
            delete temp[kk];
            angular.extend(yx_staffs, temp);
            $scope.yx_staffs = yx_staffs;
        });
    };
    //个人权限设置
    //$表式一行数据
    $scope.limit_template = function () {
        return "<span style='cursor:pointer;' ng-mouseleave='removelimit()' ng-mouseenter='editlimit($event)'><img onmouseenter=event.stopPropagation() ng-if=$.allowlogin onmouseleave=event.stopPropagation() src='images/limit.png'/></span>";
    }();
    var element = null;
    $scope.removelimit = function () {
        element && function () {
            var temp = element;
            var timeout_count = setTimeout(function () {
                var e = temp[0];
                temp.scope().$destroy();
                e.parentNode && e.parentNode.removeChild(e);
            }, 600);
            var cancel_quit = function () {
                clearTimeout(timeout_count);
            };
            element.cancel_quit = cancel_quit;
        }();
    };

    $scope.editlimit = function ($event) {
        if (!$event || element && element.target === target) {
            return element.cancel_quit && element.cancel_quit();
        }
        var target = $event && $event.target;
        var div = [
            "<div class=limit-box ng-mouseenter=editlimit() ng-mouseleave=removelimit()>",
            '<div ng-click="$copy.cbjm=1-$copy.cbjm"><checkbox ng-model=$copy.cbjm disabled>成本加密</checkbox></div>',
            '<div ng-click="$copy.dy=1-$copy.dy"><checkbox ng-model=$copy.dy disabled>打印</checkbox></div>',
            '<div ng-click="$copy.dc=1-$copy.dc"><checkbox ng-model=$copy.dc disabled>导出</checkbox></div>',
            "<div><div>查询天数</div><number-input ng-model='$copy.cxts' onconfirm='confirmlimit($copy,$)'></number-input>天</div>",
            "</div>"
        ].join("");
        var scp = $scope.$new();
        var $ = angular.element($event.target).scope().$;
        scp.$ = $;
        //处理服务器数据
        var flags = {
            "dc": 0,
            "dy": 0,
            "cxts": 0
        };
        try {
            flags = JSON.parse($.flags);
        } catch (e) {
        }
        angular.extend($, flags);
        scp.$copy = {};
        var update_time = -1;
        scp.$watch("$copy", function () {
            //防止频繁请求
            if (update_time === -1) {//阻止原始值触发更新
                update_time = 0;
                return;
            }
            clearTimeout(update_time);
            update_time = setTimeout(function () {
                confirmlimit(scp.$copy, scp.$);
            }, 200);
        }, true);
        scp.element = element;
        angular.extend(scp.$copy, scp.$);
        alert.point(div, target, {scope: scp, height: 200, width: 200});
        return;

        element = angular.element(div);
        var position = getScreenPosition(target);
        element.css({
            "left": position.left + target.offsetWidth + 'px',
            "top": position.top + (target.offsetHeight >> 1) + "px",
            "width": "170px",
            "height": "auto",
            marginTop: '-34px',
            position: "fixed",
            backgroundColor: "#fff",
            zIndex: 999999999
        });
        var e = element[0];
        $compile(element)(scp);
        setTimeout(function () {
            if (element[0] === e) {
                var this_view = document.getElementsByClassName("setting-staff-main")[0];
                this_view.appendChild(e);
            }
        }, 400);
    };
    $scope.$on("$destroy", function () {
        element && element.remove();
    });
    var confirmlimit = function ($copy, $) {
        /**
         * 这里$就是当前正编辑的员工中的信息，
         * 因为每个员工的平台权限是不一样的，
         * 所以数据应绑定到$上，而不是staff上
         */
        if (!$copy.cxts) {
            $copy.cxts = 0;
        }
        $copy.iscbjm = $copy.cbjm ? 1 : 0;
        $copy.isdc = $copy.dc ? 1 : 0;
        $copy.isdy = $copy.dy ? 1 : 0;
        var flags = {
            "dc": $copy.isdc,
            "dy": $copy.isdy,
            "cxts": $copy.cxts
        };

        var params = {
            ids: $copy.id,
            iscbjm: $copy.iscbjm,
            flags: flags
        };
        api('/x6/updateQxBatch.do', params).onsuccess(function (data) {
            /**
             * 在api中已经处理了data.result==="error"的情况，所以这里不用再处理
             */
            alert.success("设置成功");
            //设置成功以后，将数据更新到$scope
            angular.extend($, $copy);
        });
    };
    $scope.confirmlimit = confirmlimit;

    $scope.close = function () {
        $scope.xstc = false;
    };
    //批量设置
//    $scope.shtop = 0;
    $scope.checked = [];

    $scope.$watch('checked.length', function (len) {
        $scope.shtop = len;
        if (!len) {
            delete $scope.updateQuanXian.show;
        }
    });

    $scope.showCancel = function () {
        $scope.shtop = 0;
        delete $scope.updateQuanXian.show;
    };
    //显示安全设置
    $scope.option = function () {
    };

    //保存安全设置 批量修改权限   
    $scope.quanxian = {
        cbjm: 0,
        dy: 0,
        dc: 0,
        cxts: 10
    };
    $scope.updateQuanXian = function () {
        //alert($scope.staff.cxts);
        delete $scope.updateQuanXian.show;
        var quanxian = $scope.quanxian;
        var flags = {
            "dc": quanxian.dc,
            "dy": quanxian.dy,
            "cxts": quanxian.cxts
        };
        var ids = $scope.checked.map(function (c) {
            return c.id;
        }).join(",");
        var params = {
            ids: ids,
            iscbjm: quanxian.cbjm,
            flags: flags
        };
        api('/x6/updateQxBatch.do', params).onsuccess(function (data) {
            alert.success("设置成功");
            refresh();
        });
    };
    //批量删除    
    var bachRemove = function () {
        var checked = $scope.checked;
        alert("确定删除已选的" + checked.length + "个员工吗？", {
            "确认": function () {
                api('/x6/deleteUserBatch.do', {
                    ids: checked.map(function (obj) {
                        return obj.id;
                    }).join(',')
                }).onsuccess(function (data) {
                    alert.success("删除成功");
                    refresh();
                });
            },
            "取消": function () {

            }
        }).warn();
    };
    $scope.bachDelete = bachRemove;
//    $scope.bachDelete = function () {
//        //删除保护，在1秒之内点击两次，再删除
//        var bachDelete = $scope.bachDelete;
//        if (!bachDelete.ing) {
//            $timeout(function () {
//                bachDelete.ing = false;
//            }, 1000);
//            bachDelete.ing = true;
//            return;
//        }
//        bachDelete.ing = false;
//        var setArray = $scope.checked;
//        var idsArray = [];
//        for (var i = 0; i < setArray.length; i++) {
//            idsArray.push(setArray[i].id);
//        }
//        var params = {
//            ids: idsArray.join()
//        };
//        api('/x6/deleteUserBatch.do', params).onsuccess(function (data) {
//            alert.success("删除成功");
//
//        });
//    };
    //开通新员工
    $scope.openNewyg = function () {
        var idsArray = [];
        var setArray = $scope.checked;
        for (var i = 0; i < setArray.length; i++) {
            idsArray.push(setArray[i].id);
        }
        var params = {
            ids: idsArray.join()
        };
        api('/x6/checkUserBatch.do', params).onsuccess(function (data) {
            alert.success("开通成功");
            refresh();
        });
    };
}]
